import React, { useState, useEffect, useRef } from "react";

// 在组件外部添加 Getty Images 脚本
const addGettyScript = () => {
  const script = document.createElement('script');
  script.src = '//embed-cdn.gettyimages.com/widgets.js';
  script.charset = 'utf-8';
  script.async = true;
  document.body.appendChild(script);

  // 定义全局 gie 函数
  window.gie = window.gie || function(c) {
    (window.gie.q = window.gie.q || []).push(c);
  };
};

const data = [
  { 
    id: 1, 
    isGetty: true,
    gettyHtml: `<div class="getty embed image" style="background-color:#fff;display:inline-block;font-family:Roboto,sans-serif;color:#a7a7a7;font-size:11px;width:100%;height:100%;">
      <div style="overflow:hidden;position:relative;height:100%;width:100%;">
        <iframe 
          src="//embed.gettyimages.com/embed/1474925731?et=M69qFxwlQiBnf5gCeRWivg&tld=com&sig=FRBNKSFwo87XtFeBLiOBTZ85QVCGZ6U5aVouo8hprbQ=&caption=false&ver=1" 
          scrolling="no" 
          frameborder="0" 
          style="display:inline-block;position:absolute;top:0;left:0;width:100%;height:100%;margin:0;">
        </iframe>
      </div>
    </div>`,
    text: "Block 1",
    url: "https://example.com/1"
  },
  { id: 2, image: "/test/photo2.jpg", text: "Block 2", url: "https://example.com/2" },
  { id: 3, image: "/test/photo3.jpg", text: "Block 3", url: "https://example.com/3" },
  { id: 4, image: "/test/photo4.jpg", text: "Block 4", url: "https://example.com/4" },
  { id: 5, image: "/test/photo5.jpg", text: "Block 5", url: "https://example.com/5" },
  { id: 6, image: "/test/photo6.jpg", text: "Block 6", url: "https://example.com/6" },
  { id: 7, image: "/test/photo1.jpg", text: "Block 7", url: "https://example.com/7" },
  { id: 8, image: "/test/photo2.jpg", text: "Block 8", url: "https://example.com/8" },
];

export default function ScrollingMasonry() {
  const [isPaused, setIsPaused] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    // 添加 Getty Images 脚本
    addGettyScript();

    // 初始化 Getty Images 小部件
    if (window.gie) {
      window.gie(function(){
        window.gie.widgets.load({
          id: 'M69qFxwlQiBnf5gCeRWivg',
          sig: 'FRBNKSFwo87XtFeBLiOBTZ85QVCGZ6U5aVouo8hprbQ=',
          w: '100%',
          h: '100%',
          items: '1474925731',
          caption: false,
          tld: 'com',
          is360: false
        });
      });
    }

    const container = containerRef.current;
    let scrollInterval;

    if (!isPaused) {
      scrollInterval = setInterval(() => {
        if (container) {
          container.scrollTop += 1;
          if (container.scrollTop >= container.scrollHeight - container.clientHeight) {
            container.scrollTop = 0;
          }
        }
      }, 30);
    }

    return () => clearInterval(scrollInterval);
  }, [isPaused]);

  // 创建两倍的数据以确保滚动循环
  const duplicatedData = [...data, ...data];

  // 分成左右两列
  const leftColumn = duplicatedData.filter((_, index) => index % 2 === 0);
  const rightColumn = duplicatedData.filter((_, index) => index % 2 === 1);

  const handleClick = (url) => {
    window.open(url, '_blank');  // 在新标签页打开链接
  };

  return (
    <div
      ref={containerRef}
      className="overflow-hidden h-[630px] w-[350px] border rounded-md"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <div className="flex gap-4">
        {/* 左列 */}
        <div className="flex-1">
          {leftColumn.map((item, index) => (
            <div
              key={`left-${item.id}-${index}`}
              className="flex flex-col items-center bg-gray-100 rounded-lg mb-4 overflow-hidden cursor-pointer"
              style={{ 
                transition: 'all 0.5s ease-in-out',
                height: '300px'
              }}
              onClick={() => handleClick(item.url)}
            >
              <div className="h-[90%] w-full">
                {item.isGetty ? (
                  <div 
                    dangerouslySetInnerHTML={{ __html: item.gettyHtml }}
                    style={{ height: '100%', width: '100%' }}
                  />
                ) : (
                  <img
                    src={item.image}
                    alt={item.text}
                    className="w-full h-full object-cover"
                  />
                )}
              </div>
              <div className="h-[10%] w-full bg-gray-800 flex items-center justify-center">
                <p className="text-center font-medium text-white text-sm">{item.text}</p>
              </div>
            </div>
          ))}
        </div>

        {/* 右列 - 添加了 mt-[150px] 来错开位置 */}
        <div className="flex-1 mt-[150px]">
          {rightColumn.map((item, index) => (
            <div
              key={`right-${item.id}-${index}`}
              className="flex flex-col items-center bg-gray-100 rounded-lg mb-4 overflow-hidden cursor-pointer"
              style={{ 
                transition: 'all 0.5s ease-in-out',
                height: '300px'
              }}
              onClick={() => handleClick(item.url)}  // 添加点击处理
            >
              <div className="h-[90%] w-full">
                <img
                  src={item.image}
                  alt={item.text}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="h-[10%] w-full bg-gray-800 flex items-center justify-center">
                <p className="text-center font-medium text-white text-sm">{item.text}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
