import React, { useState, useEffect, useRef } from "react";

const data = [
  { id: 1, image: "/test/photo1.jpg", text: "Block 1" },
  { id: 2, image: "/test/photo2.jpg", text: "Block 2" },
  { id: 3, image: "/test/photo3.jpg", text: "Block 3" },
  { id: 4, image: "/test/photo4.jpg", text: "Block 4" },
  { id: 5, image: "/test/photo5.jpg", text: "Block 5" },
  { id: 6, image: "/test/photo6.jpg", text: "Block 6" },
  { id: 7, image: "/test/photo1.jpg", text: "Block 7" },
  { id: 8, image: "/test/photo2.jpg", text: "Block 8" },
];

export default function ScrollingMasonry() {
  const [isPaused, setIsPaused] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;
    let scrollInterval;

    if (!isPaused) {
      scrollInterval = setInterval(() => {
        if (container) {
          container.scrollTop += 1;
          if (container.scrollTop >= container.scrollHeight - container.clientHeight) {
            container.scrollTop = 0;
          }
        }
      }, 30);
    }

    return () => clearInterval(scrollInterval);
  }, [isPaused]);

  // 创建两倍的数据以确保滚动循环
  const duplicatedData = [...data, ...data];

  // 分成左右两列
  const leftColumn = duplicatedData.filter((_, index) => index % 2 === 0);
  const rightColumn = duplicatedData.filter((_, index) => index % 2 === 1);

  return (
    <div
      ref={containerRef}
      className="overflow-hidden h-[630px] w-[350px] border rounded-md"
      onMouseEnter={() => setIsPaused(true)}
      onMouseLeave={() => setIsPaused(false)}
    >
      <div className="flex gap-4 px-4">
        {/* 左列 */}
        <div className="flex-1">
          {leftColumn.map((item, index) => (
            <div
              key={`left-${item.id}-${index}`}
              className="flex flex-col items-center bg-white shadow-md p-4 rounded-lg mb-4"
              style={{ 
                transition: 'all 0.5s ease-in-out'
              }}
            >
              <img
                src={item.image}
                alt={item.text}
                className="w-full h-auto rounded-lg mb-2"
              />
              <p className="text-center font-medium text-gray-700">{item.text}</p>
            </div>
          ))}
        </div>

        {/* 右列 */}
        <div className="flex-1">
          {rightColumn.map((item, index) => (
            <div
              key={`right-${item.id}-${index}`}
              className="flex flex-col items-center bg-white shadow-md p-4 rounded-lg mb-4"
              style={{ 
                transition: 'all 0.5s ease-in-out'
              }}
            >
              <img
                src={item.image}
                alt={item.text}
                className="w-full h-auto rounded-lg mb-2"
              />
              <p className="text-center font-medium text-gray-700">{item.text}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
