import React, { useState, useRef } from "react";
import "./SharePage.css";
import Chatbot from "./Chatbot";
import ScrollingMasonry from "./ScrollingMasonry";

function SharePage() {
  const [isChatbotOpen, setIsChatbotOpen] = useState(false);
  const descriptionRef = useRef(null);

  const toggleChatbot = () => {
    setIsChatbotOpen(!isChatbotOpen);
  };

  const description = "这里是投手的详细描述..."; // 你的实际描述文本

  return (
    <div className="background">
      <div className="content">
        <div className="left-section">
          <h1>ROKI SASAKI</h1>
          <div className="description-container">
            <p 
              ref={descriptionRef} 
              className="description"
              style={{
                maxHeight: isExpanded ? 'none' : '100%',
                overflow: 'hidden'
              }}
            >
              {description}
            </p>
            {checkOverflow() && !isExpanded && (
              <button 
                className="show-more-button"
                onClick={() => setIsExpanded(true)}
              >
                Show More
              </button>
            )}
          </div>

          <div className="chat-button" onClick={toggleChatbot}>
            <img 
                src="/test/chat.png"
                alt="Chat Icon"
                className="chat-icon"
            />  
            <span>Chat with Us</span>
          </div>

        </div>

        <div className="center-section">
        <img
                src="/test/image2.png"
                alt="MLB Player"
            className="pitcher-image"
            />
        </div>

        <div className="right-section">
          <ScrollingMasonry />
        </div>

      </div>

      {isChatbotOpen && <Chatbot onClose={() => setIsChatbotOpen(false)} />}
    </div>
  );
}

export default SharePage;
