import React, { useState, useEffect } from "react";
import "./SharePage.css";

function SharePage() {
const galleryImages = [
    "/path-to-image1.jpg",
    "/path-to-image2.jpg",
    "/path-to-image3.jpg",
    "/path-to-image4.jpg",
    "/path-to-image5.jpg",
  ];

  const extendedImages = [
    galleryImages[galleryImages.length - 1], 
    ...galleryImages,
    galleryImages[0], 
  ];

  const [currentIndex, setCurrentIndex] = useState(0); // 初始索引设为 1，对应实际的第一张图片
  const [isTransitioning, setIsTransitioning] = useState(false); // 控制是否启用动画

  const handleNext = () => {
    if (!isTransitioning) {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  };

  const handlePrev = () => {
    if (!isTransitioning) {
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };

  // 自动播放
  useEffect(() => {
    const interval = setInterval(handleNext, 5000); // 每5秒切换
    return () => clearInterval(interval); // 组件卸载时清除定时器
  }, []);

  // 无缝滚动逻辑：到辅助图片时，瞬间跳到实际图片
  useEffect(() => {
    if (currentIndex === 0) {
      setTimeout(() => {
        setIsTransitioning(false); // 禁用动画
        setCurrentIndex(galleryImages.length); // 跳到实际的最后一张
      }, 300); // 等待动画结束（300ms）
    } else if (currentIndex === extendedImages.length - 1) {
      setTimeout(() => {
        setIsTransitioning(false); // 禁用动画
        setCurrentIndex(1); // 跳到实际的第一张
      }, 300); // 等待动画结束
    } else {
      setIsTransitioning(true); // 启用动画
    }
  }, [currentIndex, extendedImages.length, galleryImages.length]);


  return (
    <div className="mlb-showcase">
      {/* Left top corner text */}
        <div className ="top-left-logo">
            <img 
                src="/test/logo.png"
                alt="Team logo"
                className="team-logo-image"
            />
        </div>

        <div className="top-left-text">
            <h2>MLB Star of the Week</h2>
            <p>Highlighting the best moments in MLB</p>
        </div>

      {/* Main player section */}
      <div className="main-player">
        <h1 className="player-title">Player Name</h1>
        <div className="player-image">
            <img
                src="/test/Image.png"
                alt="MLB Player"
            className="player-image"
            />
        </div>
       </div>


      {/* Left bottom corner video */}
      <div className="bottom-left-video">
        <video controls>
          <source src="/path-to-video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>

      {/* Right top corner image */}
      <div className="top-right-image">
        <img
          src="/path-to-additional-image.png" // Replace with the URL of the additional image
          alt="Additional Content"
        />
      </div>

      {/* Right bottom corner audio and chatbot */}
      <div className="bottom-right-audio-chatbot">
        <audio controls>
          <source src="/path-to-audio.mp3" type="audio/mpeg" />
          Your browser does not support the audio tag.
        </audio>
        <div className="chatbot">
          <p>Chat with our MLB Bot!</p>
          <textarea placeholder="Type your message here..." />
          <button>Send</button>
        </div>
      </div>

      <div className="photo-gallery">
        <div className="arrow left" onClick={handlePrev}>
          &#9664;
        </div>
        <div
          className="gallery-container"
          style={{
            transform: `translateX(-${currentIndex * 33.33}%)`,
            transition: isTransitioning ? "transform 0.3s ease-in-out" : "none",
          }}
        >
          {galleryImages.map((src, index) => (
            <div className="gallery-item" key={index}>
              <img src={src} alt={`Gallery ${index + 1}`} />
            </div>
          ))}
        </div>
        <div className="arrow right" onClick={handleNext}>
          &#9654;
        </div>
      </div>
    </div>
  );
}

export default SharePage;
