import React, { useState, useEffect } from "react";
import "./SharePage.css";
import Chatbot from "./Chatbot";
import ScrollingMasonry from "./ScrollingMasonry";

function SharePage() {
const galleryImages = [
    "/test/photo1.jpg",
    "/test/photo2.jpg",
    "/test/photo3.jpg",
    "/test/photo4.jpg",
    "/test/photo5.jpg",
  ];

  const extendedImages = [
    galleryImages[galleryImages.length - 1], 
    ...galleryImages,
    galleryImages[0], 
  ];

  const [currentIndex, setCurrentIndex] = useState(1); 
  const [isTransitioning, setIsTransitioning] = useState(false); 

  const handleNext = () => {
    if (!isTransitioning) {
      setCurrentIndex((prevIndex) => prevIndex + 1);
    }
  };

  const handlePrev = () => {
    if (!isTransitioning) {
      setCurrentIndex((prevIndex) => prevIndex - 1);
    }
  };


  useEffect(() => {
    const interval = setInterval(handleNext, 5000); 
    return () => clearInterval(interval); 
  }, []);

  useEffect(() => {
    if (currentIndex === 0) {
      setTimeout(() => {
        setIsTransitioning(false); 
        setCurrentIndex(galleryImages.length); 
      }, 300); 
    } else if (currentIndex === extendedImages.length - 1) {
      setTimeout(() => {
        setIsTransitioning(false); 
        setCurrentIndex(1); 
      }, 300); 
    } else {
      setIsTransitioning(true); 
    }
  }, [currentIndex, extendedImages.length, galleryImages.length]);


  return (
    <div className="share-page">
      <div className="content">
        <div className="center-section">
          <div className="header-container">
            <img src="/path/to/team-logo.png" alt="Team Logo" className="team-logo" />
            <h1>Pitcher Name</h1>
          </div>
          <img src="/path/to/pitcher-image.png" alt="Pitcher" className="pitcher-image" />
        </div>
        <div className="right-section">
          <ScrollingMasonry />
        </div>
      </div>
    </div>
  );
}

export default SharePage;
