import React, { useState } from "react";
import "./SharePage.css";

function SharePage() {
  const [expandedSection, setExpandedSection] = useState(null);

  const handleSectionClick = (section) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  return (
    <div className="mlb-showcase">
      {/* Top Left Logo Section */}
      <div
        className={`section top-left-section ${
          expandedSection === "logo" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("logo")}
      >
        <img src="/test/logo.png" alt="Team Logo" className="team-logo" />
      </div>

      {/* Top Left Text Section */}
      <div
        className={`section top-left-text ${
          expandedSection === "text" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("text")}
      >
        <h2>MLB Star of the Week</h2>
        <p>Highlighting the best moments in MLB</p>
      </div>

      {/* Main Player Section */}
      <div
        className={`section main-player ${
          expandedSection === "player" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("player")}
      >
        <h1 className="player-title">Player Name</h1>
        <img
          src="/test/Image.png"
          alt="MLB Player"
          className="player-image"
        />
      </div>

      {/* Bottom Left Video Section */}
      <div
        className={`section bottom-left-video ${
          expandedSection === "video" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("video")}
      >
        <video controls>
          <source src="/path-to-video.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>

      {/* Top Right Image Section */}
      <div
        className={`section top-right-image ${
          expandedSection === "image" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("image")}
      >
        <img
          src="/path-to-additional-image.png"
          alt="Additional Content"
          className="additional-image"
        />
      </div>

      {/* Bottom Right Chatbot Section */}
      <div
        className={`section bottom-right-chatbot ${
          expandedSection === "chatbot" ? "expanded" : ""
        }`}
        onClick={() => handleSectionClick("chatbot")}
      >
        <audio controls>
          <source src="/path-to-audio.mp3" type="audio/mpeg" />
          Your browser does not support the audio tag.
        </audio>
        <div className="chatbot">
          <p>Chat with our MLB Bot!</p>
          <textarea placeholder="Type your message here..." />
          <button>Send</button>
        </div>
      </div>
    </div>
  );
}

export default SharePage;
